    display: none!important;
}



#gt-appbar #gt-appname,
.gt-src-cc-normal {
    color: #333!important;
}

.jfk-button-standard,
.goog-menuitem, 
.goog-tristatemenuitem, 
.goog-filterobsmenuitem,
.ita-kd-menuitem,
.gt-is-tr,
.gt-ct-text
{
    color: #555!important;
}

.gt-rw-div,
.gt-def-row,
.gt-syn-span,
.gt-ex-text,
.goog-option-selected .goog-menuitem-content,
.goog-menuitem-highlight .goog-menuitem-content{
    color: #999!important;
}

.gt-baf-word-clickable,
.gt-baf-hl,
.gt-related-suggest-message,
.gt-revert-correct-message,
.share-panel li a span,
.share-panel h3,
.gt-spell-correct-message,
#t-user-edit .cp-promo-link-subtext,
#gt-pb-tb,
.alt-edited
{
    color: #aaa!important;
}

#source,
#result_box,
.gt-card-ttl-txt,
.gt-is-tr-on .gt-is-sg,
#contribute-target.short_text 
{
    color: #ddd!important;
}




.jfk-button-standard.jfk-button-checked, 
.jfk-button-standard.jfk-button-clear-outline.jfk-button-checked,
.jfk-button-standard:hover,
.jfk-button-standard.jfk-button-hover,
.jfk-button-standard.jfk-button-clear-outline.jfk-button-hover,
.goog-menu,
#pb-tool
{
    background-image: none!important;
    background: #111!important;
    border-color: #181818!important;
    color: #777!important;
		
}

.goog-flat-menu-button {
    background-image: none!important;
    background: #181818!important;
    border-color: #222!important;
    height: 27px!important;
}

.goog-flat-menu-button.goog-flat-menu-button-hover,
.goog-flat-menu-button.goog-flat-menu-button-focused {
		background-image: none!important;
    background: #111!important;
    border-color: #242424!important;
}

#gt-sl-gms-menu 
.goog-menuitem-group 
.goog-option-selected, 
#gt-tl-gms-menu .goog-menuitem-group 
.goog-option-selected {
    background: #181818!important;
    color: #777!important;
    outline: none!important;
}

#gt-sl-gms-menu .goog-menuitem-group .goog-menuitem-highlight,
#gt-tl-gms-menu .goog-menuitem-group .goog-menuitem-highlight,
.goog-menuitem-emphasize-highlight,
.jfk-button-action:active,
.goog-toolbar-menu-button-hover,
.goog-toolbar-menu-button-focused 
{
	 	background-image: none!important;
    background: #181818!important;
    border-color: #181818!important;
		
		
}


.ita-kd-icon-button.ita-kd-selected.ita-kd-dropdown,
.ita-kd-inputtools-div:hover .ita-kd-icon-button,
.ita-kd-inputtools-div {
		background: transparent!important;
    border-color: #181818
}


.jfk-button-action {
    background-image: none!important;
    background: #000!important;
    border-color: #181818!important;
    color: #aaa!important;
}

.jfk-button-action:focus {
		background-image: none!important;
    box-shadow: inset 0 0 0 1px #0000000d!important;
}


.cd-exp-ar:before {
    border-top-color: #181818!important;
}

.cd-exp-ar:after {
    border-top-color: #333!important;
}


.st-stp1-text {
    background: #181818!important;
    border-color: #181818!important;
    color: #aaa!important;
}


.goog-toolbar-button-active,
.goog-toolbar-button-hover,
t-served-community-button {
    background: none!important;
    background-image: none!important;
    border-color: #181818!important;
    color: #aaa;
}

.jfk-tooltip,
.jfk-bubble {
    border-color: rgba(255,255,255,0.04)!important;
    background: #111;
    color: #aaa;
}

.trans-target-highlight,
.gt-hl-text {
    background: #181818!important;
    color: #ddd!important;
}

.share-panel a {
    outline: none;
}

.goog-toolbar-button, .goog-toolbar-menu-button {
    height: 100%!important;
}

.trans-edit {
    border-color: #181818;
    background: none;
    color: #ddd;
}

#gt-pb-sb,
.jfk-checkbox {
    background: #181818;
    border-color: #222;
    color: #ddd;
}

#gt-c a,
#gt-src-listen, 
#gt-res-listen 
{
    background: transparent!important;
    border-color: #181818!important;
}

.jfk-tooltip-arrowdown .jfk-tooltip-arrowimplbefore, 
.jfk-tooltip-arrowup .jfk-tooltip-arrowimplbefore,
.jfk-bubble-arrowup .jfk-bubble-arrowimplbefore
{
    border-color: rgba(0,0,0,0.5) transparent;
}

.jfk-tooltip-arrowdown .jfk-tooltip-arrowimplafter, 
.jfk-tooltip-arrowup .jfk-tooltip-arrowimplafter,
.jfk-bubble-arrowup .jfk-bubble-arrowimplafter
{
    border-color: #111 transparent;
}

.vk-btn {
    background-image: none!important;
    background: #181818;
    color: #777;
    border-color: rgba(0,0,0,0.5);
}

/* Watch next sidebar */
#related.ytd-watch {
    display: none;
}

/* Watch next panel over the that has just finished */
.ytp-endscreen-content {
    display: none;
}

/* Suggesting videos whilst the current one is starting */
.ytp-cards-teaser {
    display: none;
}

/* Suggesting videos whilst current video is playing */
.ytp-ce-element-show.ytp-ce-bottom-left-quad {
    display: none;
}

/* Centralize videos */
#top.ytd-watch {
    position: relative;
    left: 195px;
}

/* Misc crap - Share, thumbs up/down, */
#menu-container .ytd-video-primary-info-renderer {
    display: none;
}

/* Advertising concert tickets */
#ticket-shelf {
    display: none;
}

/* Home page videos */
.ytd-browse {
    display: none;
}

/* Alerts/notifications */
.notification-button-style-type-default {
    display: none;
}

/* The other icons */
ytd-topbar-menu-button-renderer yt-icon {
    display: none;
}

/* YouTube Red */
ytd-guide-section-renderer:nth-of-type(4) {
    display: none;
}

/* Trending */
ytd-guide-entry-renderer:nth-of-type(2) {
    display: none;
}

/* (i) in the top right */
.ytp-cards-button-icon-default {
    display: none;
}

/* Subscribe button with logo */
.annotation.annotation-type-custom.iv-branding {
    display: none;
}

/* Video annotations */
.annotation,
.video-annotations {
    display: none !important;
}
